package ifstatements;

public class Equality {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//WAP to validate addition of 2 numbers is equal to the given number
		int num=9,num1=5,num2=4;
		if(num1+num2==num) {
			System.out.println("Addition of 2 numbers "+num1+" and "+num2+" is equal to the given number "+num);	
			}
			else {
				System.out.println("Addition of 2 numbers "+num1+" and "+num2+" is NOT equal to the given number "+num);	
			}


	}

}
