package whileloop;

public class test1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// TODO Auto-generated method stub
        //1 2 3
		 //4 5 6
		 //7 8 9
		int i=1,x=1;
		while(i<=3) {
			int j=1;
			while(j<=3) {
				System.out.print(x+" ");
				j++;
				x++;
			}
			System.out.println();
			i++;
		}

	}

}
